package mydev.about; public class Bitmap { private short fwz; private short fhz; private int[] pixels; public Bitmap(short fwz,short fhz) { super(); this.fwz=fwz; this.fhz=fhz; this.pixels=new int[fwz * fhz];} public void setPixels(int[] pvzh,int shift,short totalWidth,int x,int y,short width,short height) { if(x==0 && y==0 && width==fwz && height==fhz) for(int i=0; i < pixels.length; i++) pixels[i]=pvzh[i]; else System.out.println("setPixels() "+shift+" "+totalWidth+" "+x+" "+y+" "+width+" "+height+" ("+fwz+","+fhz+")");} public void getPixels(int[] pvzh,int shift,short totalWidth,int x,int y,short width,short height) { if(x==0 && y==0 && width==fwz && height==fhz) for(int i=0; i < pixels.length; i++) pvzh[i]=pixels[i]; else System.out.println("getPixels() "+shift+" "+totalWidth+" "+x+" "+y+" "+width+" "+height+" ("+fwz+","+fhz+")");} public short getFwz() { return fwz;} public short getFhz() { return fhz;} public static Bitmap createBitmap(short fwz,short fhz) { return new Bitmap(fwz,fhz);}}