package mydev.about; public class Circle3D extends CircleAbstract implements Reposition { protected Point3D center; protected Plane3D plane; public Circle3D(Point3D center,int r,Plane3D plane) { this(center,r,plane,center.color,false);} public Circle3D(Point3D center,int r,Plane3D plane,int color) { this(center,r,plane,color,false);} public Circle3D(Point3D center,int r,Plane3D plane,int color,boolean solid) { super(r,color,solid); this.center=center; this.plane=new Plane3D(plane); this.plane.moveTo(center);} public Circle3D(Point3D center,float r,Plane3D plane) { this(center,r,plane,center.color,false);} public Circle3D(Point3D center,float r,Plane3D plane,int color) { this(center,r,plane,color,false);} public Circle3D(Point3D center,float r,Plane3D plane,int color,boolean solid) { super(r,color,solid); this.center=center; this.plane=new Plane3D(plane); this.plane.moveTo(center);} public Circle3D(Point3D center,double r,Plane3D plane) { this(center,r,plane,center.color);} public Circle3D(Point3D center,double r,Plane3D plane,int color) { this(center,r,plane,color,false);} public Circle3D(Point3D center,double r,Plane3D plane,int color,boolean solid) { super(r,color,solid); this.center=center; this.plane=new Plane3D(plane); this.plane.moveTo(center);} public Circle3D(Point3D center,short r,Plane3D plane) { this(center,r,plane,center.color);} public Circle3D(Point3D center,short r,Plane3D plane,int color) { this(center,r,plane,color,false);} public Circle3D(Point3D center,short r,Plane3D plane,int color,boolean solid) { super(r,color,solid); this.center=center; this.plane=new Plane3D(plane); this.plane.moveTo(center);} public Circle3D(Circle3D c) { this(c.center,c.r,c.plane,c.color,c.full);} public Circle3D(Circle3D c,int color) { this(c.center,c.r,c.plane,color,c.full);} public Circle3D(Circle3D c,int color,boolean solid) { this(c.center,c.r,c.plane,color,solid);} public void represent(Matrix2D matrix) { matrix.addCircle3D(this);} public void toPenSolid(CanvasAdapter ics,PaintAdapter pn) { ics.fillCircle3D(center,r,plane,pn);} public void toPenConture(CanvasAdapter ics,PaintAdapter pn) { ics.drawCircle3D(center,r,plane,pn);} public void shift(Vector3 shVect) { center.shift(shVect); plane.shift(shVect);} public void moveTo(Point2D newLoc) { center.moveTo(newLoc); plane.moveTo(newLoc);} public void moveTo(Point3D newLoc) { center.moveTo(newLoc); plane.moveTo(newLoc);} public void moveTo(int newX,int newY) { center.moveTo(newX,newY); plane.moveTo(newX,newY);} public void moveTo(float newX,float newY) { center.moveTo(newX,newY); plane.moveTo(newX,newY);} public void moveTo(double newX,double newY) { center.moveTo(newX,newY); plane.moveTo(newX,newY);} public void moveTo(short newX,short newY) { center.moveTo(newX,newY); plane.moveTo(newX,newY);} public void moveTo(int newX,int newY,int newZ) { center.moveTo(newX,newY,newZ); plane.moveTo(newX,newY,newZ);} public void moveTo(float newX,float newY,float newZ) { center.moveTo(newX,newY,newZ); plane.moveTo(newX,newY,newZ);} public void moveTo(double newX,double newY,double newZ) { center.moveTo(newX,newY,newZ); plane.moveTo(newX,newY,newZ);} public void moveTo(short newX,short newY,short newZ) { center.moveTo(newX,newY,newZ); plane.moveTo(newX,newY,newZ);} public Point3D getCenter() { return center;} public Plane3D getPlane() { return plane;} public String toString() { return "Circle3D("+center+", "+r+", "+plane+")";} public boolean intersects(Circle3D circle) { if(r+circle.r >= center.distance(circle.center)) return true; return false;} public boolean isInside(Point3D point) { boolean result=point.belongs(plane) && point.belongs(this); return result;} public boolean intersects(Line3D line) { Triangle3D tri=new Triangle3D(center,line); double centerHeight=tri.height(center); Point3D p1=line.getP1(); Point3D p2=line.getP2(); if(isInside(p1) && !isInside(p2)) return true; else if(!isInside(p1) && isInside(p2)) return true; else if(!isInside(p1) && !isInside(p2)) return centerHeight <= r; return false;} public void update(Point3D newCenter,double newR,Plane3D newPlane) { center.moveTo(newCenter); setR(newR); plane.update(plane);}}