package mydev.about; public class Color { protected int r; protected int g; protected int b; public Color(int r,int g,int b) { super(); this.r=r; this.g=g; this.b=b;} public Color(int rgb) { super(); java.awt.Color c1=new java.awt.Color(rgb); this.r=c1.getRed(); this.g=c1.getGreen(); this.b=c1.getBlue();} public Color middle(Color other) { Color newColor=new Color((r+other.r) / 2,(g+other.g) / 2,(b+other.b) / 2); return newColor;} public static int rgb(int rr,int gg,int bb) { Color c1=new Color(rr,gg,bb); return c1.rgb();} public int rgb() { return new java.awt.Color(r,g,b).getRGB();} public static Color valueOf(float rr,float gg,float bb) { Color c1=new Color((int) rr,(int) gg,(int) bb); return c1;} public int toArgb() { return rgb();} public static int middleColor(Colorfull c1,Colorfull c2) { return new Color(c1.color).middle(new Color(c2.color)).toArgb();}}