package mydev.about; public class Cube extends Sphere { protected float angleX; protected float angleY; public Cube(Point3D center,int r) { this(center,r,0f,0f,center.color,false);} public Cube(Point3D center,int r,int color,boolean solid) { this(center,r,0f,0f,color,solid);} public Cube(Point3D center,float r,int color,boolean solid) { this(center,(int)(r+0.5f),0f,0f,color,solid);} public Cube(Point3D center,int r,float angleX,float angleY) { this(center,r,angleX,angleY,center.color,false);} public Cube(Point3D center,int r,float angleX,float angleY,int color) { this(center,r,angleX,angleY,color,false);} public Cube(Point3D center,int r,float angleX,float angleY,int color,boolean solid) { super(center,r,color,solid); this.angleX=angleX; this.angleY=angleY;} public Cube(Point3D center,float r,float angleX,float angleY) { this(center,(int)(r+0.5f),angleX,angleY);} public Cube(Point3D center,float r,float angleX,float angleY,int color) { this(center,(int)(r+0.5f),angleX,angleY,color);} public Cube(Point3D center,float r,float angleX,float angleY,int color,boolean solid) { this(center,(int)(r+0.5f),angleX,angleY,color,solid);} public Cube(Point3D center,short r,float angleX,float angleY) { this(center,(int) r,angleX,angleY);} public Cube(Point3D center,short r,float angleX,float angleY,int color) { this(center,(int) r,angleX,angleY,color);} public Cube(Point3D center,short r,float angleX,float angleY,int color,boolean solid) { this(center,(int) r,angleX,angleY,color,solid);} public Cube(Cube c) { super(c);} public void represent(Matrix2D matrix) { matrix.addCube(this);} public void toPenSolid(Canvas ics,Paint pn) { ics.fillCube(center.getX(),center.getY(),center.getZ(),r,angleX,angleY,pn);} public void toPenConture(Canvas ics,Paint pn) { ics.drawCube(center.getX(),center.getY(),center.getZ(),r,angleX,angleY,pn);} public float getAngleX() { return angleX;} public float getAngleY() { return angleY;} public void update(float angleX,float angleY) { this.angleX=angleX; this.angleY=angleY;} public void update(Point3D center,int r,float angleX,float angleY) { super.update(center,r); update(angleX,angleY);} public void update(Point3D center,float r,float angleX,float angleY) { super.update(center,r); update(angleX,angleY);} public void update(Point3D center,short r,float angleX,float angleY) { super.update(center,r); update(angleX,angleY);} public Sphere intSphere() { return new Sphere(center,r,color,full);} public Sphere extSphere() { return new Sphere(center,(float) Math.sqrt(r * r+r * r+r * r),color,full);} public boolean equals(Object o) { if(o !=null && o instanceof Cube) if(super.equals((Sphere) o) && angleX==((Cube) o).angleX && angleY==((Cube) o).angleY) return true; return false;} public String toString() { return "Cube("+center+", "+r+", "+angleX+", "+angleY+")";}}