package mydev.about; public abstract class FillFullable extends Colorfull { protected boolean full; public FillFullable(int color,boolean solid) { super(color); this.full=solid;} public FillFullable(int color) { super(color);} public boolean getFill() { return full;} public void setFill(boolean solid) { this.full=solid;} public void toPen(Canvas ics,Paint pn) { if(full) toPenSolid(ics,pn); else toPenConture(ics,pn);} public abstract void toPenSolid(Canvas ics,Paint pn); public abstract void toPenConture(Canvas ics,Paint pn);}