package mydev.about; public class Line3D extends LineAbstract implements Reposition { protected Point3D p1; public Line3D(Point3D p1,Point3D p2) { this(p1,p2,Color.middleColor(p1,p2));} public Line3D(Point3D p1,Point3D p2,int color) { super(color); this.p1=p1; updateVector(p2);} public Line3D(Point3D p1,Vector3 v1) { this(p1,v1,p1.color);} public Line3D(Point3D p1,Vector3 v1,int color) { super(color); this.p1=p1; this.vect=v1;} public Line3D(Line3D l) { this(l,l.color);} public Line3D(Line3D l,int color) { super(l.color); this.p1=l.p1; this.vect=l.vect;} public void represent(Matrix2D matrix) { matrix.addLine(this);} public void toPen(Canvas ics,Paint pn) { Point3D p2tmp=getP2(); ics.drawLine(p1.getX(),p1.getY(),p1.getZ(),p2tmp.getX(),p2tmp.getY(),p2tmp.getZ(),pn);} public void shift(Vector3 shVect) { p1.shift(shVect);} public void moveTo(Point2D newLoc) { p1.moveTo(newLoc);} public void moveTo(Point3D newLoc) { p1.moveTo(newLoc);} public void moveTo(int newX,int newY) { p1.moveTo(newX,newY);} public void moveTo(float newX,float newY) { p1.moveTo(newX,newY);} public void moveTo(double newX,double newY) { p1.moveTo(newX,newY);} public void moveTo(short newX,short newY) { p1.moveTo(newX,newY);} public void moveTo(int newX,int newY,int newZ) { p1.moveTo(newX,newY,newZ);} public void moveTo(float newX,float newY,float newZ) { p1.moveTo(newX,newY,newZ);} public void moveTo(double newX,double newY,double newZ) { p1.moveTo(newX,newY,newZ);} public void moveTo(short newX,short newY,short newZ) { p1.moveTo(newX,newY,newZ);} public Point3D getP1() { return p1;} public Point3D getP2() { return vect.destination(p1);} public Vector3 updateVector(Point3D p2) { this.vect=Vector3.shift(p2,p1); return vect;} public Point3D midpoint() { return vect.half().destination(p1);} public Point3D midpointProportion(double proport) { return vect.halfLikeProportion(proport).destination(p1);} public boolean belongs(Plane3D plane) { boolean result=getP1().belongs(plane) && getP2().belongs(plane); return result;} public boolean intersects(Plane3D plane) { return false;} public Point3D getIntersect(Plane3D plane) { return null;} public boolean intersects(Line3D line) { boolean result=new Triangle3D(getP1(),line).area()+new Triangle3D(getP2(),line).area()==new Triangle3D(line.getP1(),this).area()+new Triangle3D(line.getP2(),this).area(); return result;} public Point3D findIntersection(Line3D line) { if(intersects(line)) { if(line.length() > 1 && length() > 1) { Line3D[] AA=line.halves(); Line3D[] BB=halves(); Point3D r1=AA[0].findIntersection(BB[0]); if(r1 !=null) return r1; Point3D r2=AA[0].findIntersection(BB[1]); if(r2 !=null) return r2; Point3D r3=AA[1].findIntersection(BB[0]); if(r3 !=null) return r3; Point3D r4=AA[1].findIntersection(BB[1]); if(r4 !=null) return r4;} else return new Line3D(midpoint(),line.midpoint()).midpoint();} return null;} public Line3D[] halves() { Point3D mp=midpoint(); Line3D first=new Line3D(getP1(),mp); Line3D second=new Line3D(getP2(),mp); Line3D[] result=new Line3D[1+1]; result[0]=first; result[1]=second; return result;} public boolean intersects(Cube cube) { return false;} public Point3D[] getIntersect(Cube cube) { return null;} public boolean intersects(Sphere sphere) { return sphere.intersects(this);} public Point3D[] getIntersect(Sphere sphere) { return null;} public boolean intersects(Circle3D circle) { return circle.intersects(this);} public Triangle3D triangle(Point3D point) { return new Triangle3D(point,this,getColor());} public boolean equals(Object o) { if(o !=null && o instanceof Line3D) { if(p1.equals(((Line3D) o).p1) && getP2().equals(((Line3D) o).getP2())) return true; if(p1.equals(((Line3D) o).getP2()) && getP2().equals(((Line3D) o).p1)) return true;} return false;} public String toString() { return "Line("+p1+", "+vect+")";} public static Line3D createLine(Point3D start,Point3D direction,long length) { Line3D result=createLine(start,direction,(double) length); return result;} public static Line3D createLine(Point3D start,Point3D direction,double length) { Line3D patternLine=new Line3D(start,direction); Vector3 ptrnVect=patternLine.getVect(); Vector3 resultVect=ptrnVect.halfLikeProportion(length / ptrnVect.module()); Point3D finish=resultVect.getShiftedCopy(start); Line3D result=new Line3D(start,finish); return result;} public Point3D A() { return getP1();} public Point3D B() { return getP2();} public Line3D AB() { return new Line3D(A(),B());} public Vector3 lineVector(Point3D startPoint) { if(startPoint.equals(p1)) return getVect(); return getBackVect();} public void update(Point3D p1,Point3D p2) { this.p1.moveTo(p1); this.vect=Vector3.shift(p2,p1);} public Point3D labelA(int between) { Vector3 abVectBack=new Line3D(A(),B()).getVect().backVector(); Vector3 reverse=abVectBack.halfLikeProportion((float) between / abVectBack.module()); Point3D labelPoint=reverse.getShiftedCopy(A()); return labelPoint;} public Point3D labelB(int between) { Vector3 baVectBack=new Line3D(B(),A()).getVect().backVector(); Vector3 reverse=baVectBack.halfLikeProportion((float) between / baVectBack.module()); Point3D labelPoint=reverse.getShiftedCopy(B()); return labelPoint;}}