package mydev.about; import java.awt.FontMetrics; import java.awt.Graphics; public class Paint { protected int color; protected java.awt.Color curTransparentColor=Picture.FON_COLOR; protected int textSize; protected int x; protected int y; protected int width; protected int height; protected Rectangle2D area; protected Graphics cs1; public Paint() {} public Paint(Graphics cs1) { this.cs1=cs1;} public void updateGraphics(Graphics cs1) { if(cs1 !=null) this.cs1=cs1;} public void setTextSize(int size) { this.textSize=size;} public void setColor(int color) { this.color=color;} public Paint selectTransparent(java.awt.Color transparentColor) { this.curTransparentColor=transparentColor; return this;} public java.awt.Color getCurTransparentColor() { return curTransparentColor;} public Paint select(Colorfull cf) { this.color=cf.color; return this;} public Paint reshape(Rectangle2D areaWnd) { this.x=areaWnd.getUc().x; this.y=areaWnd.getUc().y; this.width=areaWnd.width; this.height=areaWnd.height; this.area=areaWnd; return this;} void getTextBounds(String textBuf,int x,int y,Rect textBounds) { if(cs1 !=null && textBounds !=null && x >= 0 && y >= 0 && textBuf !=null) { FontMetrics fm=cs1.getFontMetrics(); int textBufWidth=fm.stringWidth(textBuf); int textBufHeight=fm.getHeight(); textBounds.update(textBufWidth,textBufHeight);}} public int getX() { return x;} public int getY() { return y;} public int getWidth() { return width;} public int getHeight() { return height;} public Rectangle2D getArea() { return area;}}