package mydev.about; import java.awt.Color; import java.awt.Component; import java.awt.Font; import java.awt.FontMetrics; import java.awt.Graphics; public class Paint { private int color; private java.awt.Color colorTransparent=Picture.FON_COLOR; private int textFontSize; private int x; private int y; private int width; private int height; private Rectangle2D area; Graphics cs1; Component comp; public Paint() {} public Paint(Graphics cs1) { this.cs1=cs1;} public void updateGraphics(Graphics cs1) { if(cs1 !=null) this.cs1=cs1;} public void setTextSize(int size) { this.textFontSize=size;} public void setColor(int color) { this.color=color;} public Paint selectTransparent(int colorTransparent) { this.colorTransparent=new Color(colorTransparent); return this;} public int getColorTransparent() { return colorTransparent.getRGB();} public Paint select(Colorfull cf) { this.color=cf.color; return this;} public Paint setComponent(Component comp) { this.comp=comp; return this;} public Paint reshape(Rectangle2D areaWnd) { this.x=areaWnd.getUc().x; this.y=areaWnd.getUc().y; this.width=areaWnd.width; this.height=areaWnd.height; this.area=areaWnd; return this;} void getTextBounds(String textBuf,int x,int y,Rect textBounds) { if(cs1 !=null && textBounds !=null && x >= 0 && y >= 0 && textBuf !=null && textFontSize >= 1) { FontMetrics fm=cs1.getFontMetrics(); Font font=fm.getFont(); cs1.setFont(new Font(font.getName(),font.getStyle(),textFontSize)); fm=cs1.getFontMetrics(); int textBufWidth=fm.stringWidth(textBuf); int textBufHeight=fm.getHeight(); textBounds.update(textBufWidth,textBufHeight); cs1.setFont(font);}} public int getX() { return x;} public int getY() { return y;} public int getWidth() { return width;} public int getHeight() { return height;} public Rectangle2D getArea() { return area;} public Color getColor() { return new java.awt.Color(color);}}