package mydev.about; import java.awt.Component; public class PaintAdapterAboutImpl implements PaintAdapter { private Paint proxy; private Component comp; public PaintAdapterAboutImpl(Component comp) { super(); this.proxy=new Paint(); this.comp=comp;} public PaintAdapterAboutImpl(Paint pn) { super(); this.proxy=pn;} public void setColor(int color) { proxy.setColor(color);} public void setTextSize(int textSize) { proxy.setTextSize(textSize);} public void getTextBounds(String text,int index,int count,Rect bounds) { proxy.getTextBounds(text,index,count,bounds);} public Object getRef() { return proxy;} public Object getComponentRef() { return comp;} public PaintAdapter select(Colorfull cf) { proxy.setColor(cf.getColor()); return this;} public int getColor() { return proxy.getColor().getRGB();} public PaintAdapter reshape(Rectangle2D areaWnd) { proxy.reshape(areaWnd); return this;} public int getColorTransparent() { int ct=proxy.getColorTransparent(); return ct;} public void setColorTransparent(int transpColor) { proxy.selectTransparent(transpColor);} public Rectangle2D getArea() { return proxy.getArea();} public int getWidth() { return proxy.getWidth();} public int getHeight() { return proxy.getHeight();} public PaintAdapter setComponent(Component comp) { if(comp !=null) this.comp=comp; return this;}}