package mydev.about; public class Plane2D extends Triangle2D { public Plane2D(Point2D p,Line2D l,int color,boolean solid) { super(p,l,color,solid);} public Plane2D(Point2D p,Line2D l,int color) { super(p,l,color);} public Plane2D(Point2D p,Line2D l) { super(p,l);} public Plane2D(Point2D p1,Point2D p2,Point2D p3,int color,boolean solid) { super(p1,p2,p3,color,solid);} public Plane2D(Point2D p1,Point2D p2,Point2D p3,int color) { super(p1,p2,p3,color);} public Plane2D(Point2D p1,Point2D p2,Point2D p3) { super(p1,p2,p3);} public Plane2D(Point3D p1,Vector3 a1,Vector3 b1,int color,boolean solid) { super(p1,a1,b1,color,solid);} public Plane2D(Point3D p1,Vector3 a1,Vector3 b1,int color) { super(p1,a1,b1,color);} public Plane2D(Point3D p1,Vector3 a1,Vector3 b1) { super(p1,a1,b1);} public Plane2D(Triangle2D t,int color,boolean solid) { super(t,color,solid);} public Plane2D(Triangle2D t,int color) { super(t,color);} public Plane2D(Triangle2D t) { super(t);} public void represent(Matrix2D matrix) {} public void toPenSolid(CanvasAdapter ics,PaintAdapter pn) {} public void toPenConture(CanvasAdapter ics,PaintAdapter pn) {} public String toString() { return "Plane2D("+p1+", "+a+", "+b+")";} public void update(Plane2D plane) { update(plane.getP1(),plane.getP2(),plane.getP3());} public Triangle2D getTriangle() { return this;}}