package mydev.about; import mydev.vutils.Ester; public class PlaneCross extends Colorfull { protected Point2D curMouse; protected int width; protected int height; protected Ester firstCoord; protected Ester secondCoord; public PlaneCross(Point2D curMouse,int width,int height,Ester first,Ester second,int crossColor) { super(crossColor); this.curMouse=curMouse; this.width=width; this.height=height; this.firstCoord=first; this.secondCoord=second;} public void represent(Matrix2D matrix) {} public void toPen(Canvas ics,Paint pn) { Line2D crossVert=new Line2D(new Point2D(0,curMouse.getY()),new Point2D(width-1,curMouse.getY()),color); crossVert.toPen(ics,pn.select(crossVert)); Line2D crossHorz=new Line2D(new Point2D(curMouse.getX(),0),new Point2D(curMouse.getX(),height-1),color); crossHorz.toPen(ics,pn.select(crossHorz)); String text=""+firstCoord+curMouse.getX()+" "+secondCoord+curMouse.getY(); int q=10; Text2D crossText=new Text2D(new Point2D(curMouse.getX()+q,curMouse.getY()-q),text,color); int textWidth=crossText.getWidth(ics); if(crossText.getMiddleHeightLeftPoint().getX()+textWidth >= width) crossText.getMiddleHeightLeftPoint().shift(Vector3.shiftX(-textWidth-q)); int textHeight=crossText.getHeight(ics); if(crossText.getMiddleHeightLeftPoint().getY()-textHeight <= 0) crossText.getMiddleHeightLeftPoint().shift(Vector3.shiftY(textHeight)); crossText.toPen(ics,pn.select(crossText));} public Point2D getCurMouse() { return curMouse;} public int getWidth() { return width;} public int getHeight() { return height;} public Ester getFirstCoord() { return firstCoord;} public Ester getSecondCoord() { return secondCoord;} public void setWidth(int width) { this.width=width;} public void setHeight(int height) { this.height=height;} public String toString() { return "PlaneCross [curMouse="+curMouse+", width="+width+", height="+height+", first="+firstCoord+", second="+secondCoord+"]";} public void update(Point2D newLocation,int width,int height) { getCurMouse().moveTo(newLocation); setWidth(width); setHeight(height);}}