package mydev.about; public class Rectangle2D extends FillFullable { protected Point2D uc; protected int width; protected int height; public Rectangle2D(Point2D uc,int width,int height) { this(uc,width,height,uc.color,false);} public Rectangle2D(Point2D uc,int width,int height,int color) { this(uc,width,height,color,false);} public Rectangle2D(Point2D uc,int width,int height,int color,boolean solid) { super(color,solid); this.uc=uc; this.width=width; this.height=height;} public Rectangle2D(Point2D uc,float width,float height) { this(uc,(int)(width+0.5f),(int)(height+0.5f),uc.color,false);} public Rectangle2D(Point2D uc,float width,float height,int color) { this(uc,(int)(width+0.5f),(int)(height+0.5f),color,false);} public Rectangle2D(Point2D uc,float width,float height,int color,boolean solid) { this(uc,(int)(width+0.5f),(int)(height+0.5f),color,solid);} public Rectangle2D(Point2D uc,short width,short height) { this(uc,(int) width,(int) height,uc.color,false);} public Rectangle2D(Point2D uc,short width,short height,int color) { this(uc,(int) width,(int) height,color,false);} public Rectangle2D(Point2D uc,short width,short height,int color,boolean solid) { this(uc,(int) width,(int) height,color,solid);} public Rectangle2D(Rectangle2D r) { this(r.uc,r.width,r.height,r.color,r.full);} public void represent(Matrix2D matrix) { matrix.addRectangle(this);} public void toPenSolid(Canvas ics,Paint pn) { ics.fillRect(uc.getX(),uc.getY(),width,height,pn);} public void toPenConture(Canvas ics,Paint pn) { ics.drawRect(uc.getX(),uc.getY(),width,height,pn);} public Point2D getUc() { return uc;} public int getWidth() { return width;} public void setWidth(int width) { this.width=width;} public int getHeight() { return height;} public void setHeight(int height) { this.height=height;} public void shift(Vector sh) { this.uc.shift(sh);} public void resize(int width,int height) { this.width=width; this.height=height;} public void resize(float width,float height) { this.width=(int)(width+0.5f); this.height=(int)(height+0.5f);} public void update(Point2D uc,int width,int height) { this.uc.move(uc); resize(width,height);} public void update(Point2D uc,float width,float height) { this.uc.move(uc); resize(width,height);} public void update(Point2D uc,short width,short height) { this.uc.move(uc); resize(width,height);} public Circle2D extCircle() { Vector diag=Vector.shiftXY(width,height); Point2D rd=new Point2D(uc); rd.shift(diag); Line2D diagonal=new Line2D(uc,rd); Point2D center=diagonal.middlePoint(); float r=(float)(diagonal.length() / 2f); return new Circle2D(center,r,color,full);} public boolean equals(Object o) { if(o !=null && o instanceof Rectangle2D) if(uc.equals(((Rectangle2D) o).uc) && width==((Rectangle2D) o).width && height==((Rectangle2D) o).height) return true; return false;} public String toString() { return "Rectangle("+uc+", "+width+", "+height+")";}}