package mydev.about; public class Sector2D extends Circle2D { protected int angleStart; protected int angleDt; public Sector2D(Point2D center,int r,int color,int angleStart,int angleDt,boolean fill) { super(center,r,color,fill); this.angleStart=angleStart; this.angleDt=angleDt;} public Sector2D(Point2D center,int r,int color,float angleStart,float angleDt,boolean fill) { this(center,r,color,(int)(angleStart+0.5f),(int)(angleDt+0.5f),fill);} public Sector2D(Point2D center,int r,int color,int angleStart,int angleDt) { this(center,r,color,angleStart,angleDt,false);} public void represent(Matrix2D matrix) { if(angleDt !=0) matrix.addSector(this);} public void toPenSolid(Canvas ics,Paint pn) { if(angleDt !=0) ics.fillSector(center.getX(),center.getY(),r,angleStart,angleDt,pn);} public void toPenConture(Canvas ics,Paint pn) { if(angleDt !=0) ics.drawSector(center.getX(),center.getY(),r,angleStart,angleDt,pn);}}