package mydev.about; public class Sector3D extends Cilinder3D { protected int angleStart; protected int angleDt; public Sector3D(Circle3D firstBase,Circle3D secondBase,int angleStart,int angleDt) { this(firstBase,secondBase,angleStart,angleDt,Color.middleColor(firstBase.color,secondBase.color),false);} public Sector3D(Circle3D firstBase,Circle3D secondBase,int angleStart,int angleDt,int color) { this(firstBase,secondBase,angleStart,angleDt,color,false);} public Sector3D(Circle3D firstBase,Circle3D secondBase,int angleStart,int angleDt,int color,boolean solid) { super(firstBase,secondBase,color,solid); this.angleStart=angleStart; this.angleDt=angleDt;} public void represent(Matrix2D matrix) {} public void toPen(Canvas ics,Paint pn) { super.toPen(ics,pn);} public void toPenSolid(Canvas ics,Paint pn) {} public void toPenConture(Canvas ics,Paint pn) {} public int getAngleStart() { return angleStart;} public int getAngleDt() { return angleDt;} public void setAngleStart(int angleStart) { this.angleStart=angleStart;} public void setAngleDt(int angleDt) { this.angleDt=angleDt;} public String toString() { return "Sector3D [angleStart="+angleStart+", angleDt="+angleDt+"]";}}