package mydev.bbb; import java.io.File; import mydev.aaa.IO; import mydev.vutils.Ester; import mydev.vutils.Files; import mydev.vutils.Filter; public class IOB extends IO { final static boolean D=!false; public IOB() { super();} public boolean deleteExtension(String path,String extension) { boolean result=true; Files files=new Files(); Ester where=new Ester(path); if(files.isDir(where)) { Ester[] chtoto=files.tree(where); Ester[] toDelete=new Filter().endsWith(chtoto,new Ester(extension)); if(toDelete.length > 0) for(int i=0; i < toDelete.length; i++) if(delete(toDelete[i].toString())==false) result=false;} return result;} public boolean delete(String srcFile) { File item=new File(srcFile); boolean result=item.delete(); if(D) System.out.println("Delete "+result+" "+item.getAbsolutePath()); return result;}}