package mydev.bbb; import java.awt.BorderLayout; import java.awt.Button; import java.awt.Checkbox; import java.awt.Panel; import java.awt.TextField; import mydev.anim.ColorsLogo; import mydev.uix.Props; public class PreviewRightPanel extends Panel { PreviewRightStopPanel stopPanel; ColorsLogo logoPanel; public PreviewRightPanel() { super(); setLayout(new BorderLayout()); setBackground(Props.bgTopColor); add("Center",stopPanel=new PreviewRightStopPanel()); add("South",logoPanel=new ColorsLogo());} public void refreshUpdatable() { logoPanel.refreshUpdatable();} public Button getBtStop() { return stopPanel.getBtStop();} public void disableStop() { stopPanel.disableStop();} public void enableStop() { stopPanel.enableStop();} public boolean hasLongTermContin() { return stopPanel.hasLongTermContin();} public boolean getPreview() { return stopPanel.getPreview();} public void setProgText(String progText) { stopPanel.setProgText(progText);}} class PreviewRightStopPanel extends Panel { Button btStop; Checkbox cbPreview; static boolean longTermOp; TextField tfProg; public PreviewRightStopPanel() { super(); setLayout(new BorderLayout()); setBackground(Props.bgTopColor); add("South",tfProg=new TextField()); tfProg.enable(false); add("Center",btStop=new Button("Stop")); btStop.enable(false); add("North",cbPreview=new Checkbox("Preview&Close")); cbPreview.setState(false);} public Button getBtStop() { return btStop;} public Checkbox getCbPreview() { return cbPreview;} public TextField getTfProg() { return tfProg;} public boolean getPreview() { return cbPreview.getState();} public void setProgText(String txt) { tfProg.setText(txt);} public void disableStop() { btStop.enable(!true); cbPreview.enable(true); longTermOp=!true;} public void enableStop() { btStop.enable(true); cbPreview.enable(!true); longTermOp=true;} public boolean hasLongTermContin() { return longTermOp;}}