package mydev.bbb; import java.awt.BorderLayout; import java.awt.Button; import java.awt.Checkbox; import java.awt.Color; import java.awt.FlowLayout; import java.awt.Panel; import mydev.uix.External3DPanel; import mydev.uix.Props; import mydev.uix.WidthHeight; import mydev.uix.YellowAutoButton; public class ToolbarLeftPanel extends Panel { LeftDefBtnPanel defBtnPanel; External3DPanel layoutPanel; DemoAuto demoAuto; public ToolbarLeftPanel(boolean with3D) { super(); setLayout(new BorderLayout()); setBackground(Color.gray); add("North",layoutPanel=with3D ? new External3DPanel(false,true) : new External3DPanel(false)); add("Center",defBtnPanel=new LeftDefBtnPanel()); add("South",new CenterAuto(demoAuto=new DemoAuto()));} public Mode getSelectedOknosMode() { boolean d3=layoutPanel.get3d(); boolean ext=layoutPanel.getExternal(); if(ext) return Mode.EXT; return d3 ? Mode.INT3D : Mode.INT;} public Checkbox getCbExt() { return layoutPanel.getCbExt();} public boolean getExternal() { return layoutPanel.getCbExt().getState();} public Checkbox getCbD3() { return layoutPanel.getChD3();} public boolean getD3() { return layoutPanel.getChD3().getState();} public void enableCtrls(boolean enable) { demoAuto.enableCtrl(enable); defBtnPanel.enableBtns(enable);} public LeftDefBtnPanel getDefBtnPanel() { return defBtnPanel;} public DemoAuto getDemoAuto() { return demoAuto;} public Button getBtAuto() { return getDemoAuto().getBtAuto();} public Button getBtRepo() { return getDefBtnPanel().getBtRepo();} public Button getBtDef() { return getDefBtnPanel().getBtDef();} public WidthHeight getWhPanel() { return getDefBtnPanel().getWhPanel();} public External3DPanel getExt3dPanel() { return layoutPanel;}} class LeftDefBtnPanel extends Panel { Button btDef; Button btRepo; WidthHeight whPanel; public LeftDefBtnPanel() { super(); setLayout(new BorderLayout()); setBackground(Color.gray); add("North",btDef=new Button("Default")); add("Center",btRepo=new Button("Reposition")); add("South",whPanel=WidthHeight.createPanel(new FlowLayout(FlowLayout.CENTER,1-1,1-1)));} public void enableBtns(boolean enable) { btDef.enable(enable); btRepo.enable(enable); whPanel.enableCtrls(enable);} public Button getBtDef() { return btDef;} public Button getBtRepo() { return btRepo;} public WidthHeight getWhPanel() { return whPanel;}} class DemoAuto extends YellowAutoButton { public DemoAuto() { super();}} class CenterAuto extends Panel { public CenterAuto(DemoAuto demoAuto) { setBackground(Props.bgCtrlPanelColor); setLayout(new FlowLayout(FlowLayout.CENTER,1-1+1,1-1+1)); add(demoAuto);}}