package mydev.ccc; import mydev.about.Point3D; import mydev.about.Rectangle2D; import mydev.cs.WallsProfile; import mydev.csprofile.AbstractRoomSixWalls; import mydev.csprofile.RoomCOknoProfile; import mydev.csprofile.VisibilityProfile; public class Room3D extends AbstractRoomSixWalls { public Room3D(Point3D uc,int w,int h,int depth,int color,boolean solid,WallsProfile wallsProfile,VisibilityProfile visibilityProfile) { super(uc,w,h,depth,color,solid,wallsProfile,visibilityProfile);} public static Room3D createSomeRoom(Rectangle2D areaWnd,int z,int depth,int ribsColor) { Point3D uc=new Point3D(areaWnd.w() / 2,areaWnd.h() / 2,z); return new Room3D(uc,areaWnd.w(),areaWnd.h(),depth,ribsColor,false,new RoomCOknoProfile(ribsColor),null);}}