package mydev.cs; import mydev.about.FourCornersConture2D; import mydev.about.Point2D; public class Bounds { protected short leftX; protected short topY; protected short rightX; protected short bottomY; public Bounds() { super();} public short getLeftX() { return leftX;} public void setLeftX(short leftX) { this.leftX=leftX;} public short getTopY() { return topY;} public void setTopY(short topY) { this.topY=topY;} public short getRightX() { return rightX;} public void setRightX(short rightX) { this.rightX=rightX;} public short getBottomY() { return bottomY;} public void setBottomY(short bottomY) { this.bottomY=bottomY;} public static Bounds getBounds(FourCornersConture2D conture) { Bounds cpb=new Bounds(); cpb.setLeftX((short) conture.getMinX()); cpb.setRightX((short) conture.getMaxX()); cpb.setTopY((short) conture.getMinY()); cpb.setBottomY((short) conture.getMaxY()); return cpb;} public Point2D getAvg() { Point2D avgPoint=new Point2D((leftX+rightX) / 2,(topY+bottomY) / 2); return avgPoint;} public String toString() { return "CubePlaneBounds [leftX="+leftX+", topY="+topY+", rightX="+rightX+", bottomY="+bottomY+"]";}}