package mydev.cs; import mydev.about.Canvas; import mydev.about.Matrix2D; import mydev.about.Paint; import mydev.about.Picture; public abstract class PictureWall extends ContureWall { protected Picture fillPicture; public PictureWall(int borderColor,Picture fillPicture) { super(borderColor); this.fillPicture=fillPicture;} public void particularDrawing(Canvas ics,Paint pn) { drawPicture(ics,pn);} public void particularDrawing(Matrix2D matrix) { drawPicture(matrix);} public abstract void drawPicture(Canvas ics,Paint pn); public abstract void drawPicture(Matrix2D matrix);}