package mydev.cs; public class PlaneType { public static final PlaneType PT_BACK=new PlaneType("Back"); public static final PlaneType PT_FRONT=new PlaneType("Front"); public static final PlaneType PT_LEFT=new PlaneType("Left"); public static final PlaneType PT_RIGHT=new PlaneType("Right"); public static final PlaneType PT_TOP=new PlaneType("Top"); public static final PlaneType PT_BOTTOM=new PlaneType("Bottom"); private String name; private PlaneType(String name) { super(); this.name=name;} public String getName() { return name;} public boolean isBack() { return isBack(name);} public boolean isBack(String item) { return item !=null && item.equalsIgnoreCase(PT_BACK.name);} public boolean isFront() { return isFront(name);} public boolean isFront(String item) { return item !=null && item.equalsIgnoreCase(PT_FRONT.name);} public boolean isLeft() { return isLeft(name);} public boolean isLeft(String item) { return item !=null && item.equalsIgnoreCase(PT_LEFT.name);} public boolean isRight() { return isRight(name);} public boolean isRight(String item) { return item !=null && item.equalsIgnoreCase(PT_RIGHT.name);} public boolean isTop() { return isTop(name);} public boolean isTop(String item) { return item !=null && item.equalsIgnoreCase(PT_TOP.name);} public boolean isBottom() { return isBottom(name);} public boolean isBottom(String item) { return item !=null && item.equalsIgnoreCase(PT_BOTTOM.name);} public String toString() { return "PlaneType [name="+name+"]";}}