package mydev.cs; import mydev.about.Colorfull; import mydev.about.Rectangle2D; import mydev.vutils.Queue; public abstract class WallsProfile extends Colorfull { private Queue wallsQueue=new Queue(); private ContureWall[] wallsQueueArr=new ContureWall[1-1]; protected ContureWall farSide; protected ContureWall ceiling; protected ContureWall rightSide; protected ContureWall floor; protected ContureWall leftSide; protected ContureWall nearGlass; public WallsProfile(int contureColor) { super(contureColor); initWallsQueue(contureColor);} protected void initWallsQueue(int contureColor) { wallsQueue.enqueueQueueRecord(farSide=obtainFarSide(contureColor)); wallsQueue.enqueueQueueRecord(ceiling=obtainCeiling(contureColor)); wallsQueue.enqueueQueueRecord(rightSide=obtainRightSide(contureColor)); wallsQueue.enqueueQueueRecord(floor=obtainFloor(contureColor)); wallsQueue.enqueueQueueRecord(leftSide=obtainLeftSide(contureColor)); wallsQueue.enqueueQueueRecord(nearGlass=obtainNearGlass(contureColor)); wallsQueueArr=makeCopyCash();} protected abstract ContureWall obtainFarSide(int contureColor); protected abstract ContureWall obtainCeiling(int contureColor); protected abstract ContureWall obtainRightSide(int contureColor); protected abstract ContureWall obtainFloor(int contureColor); protected abstract ContureWall obtainLeftSide(int contureColor); protected abstract ContureWall obtainNearGlass(int contureColor); public ContureWall getFarSide() { return farSide;} public ContureWall getCeiling() { return ceiling;} public ContureWall getRightSide() { return rightSide;} public ContureWall getFloor() { return floor;} public ContureWall getLeftSide() { return leftSide;} public ContureWall getNearGlass() { return nearGlass;} protected ContureWall[] makeCopyCash() { Object[] queueRecordsArray=wallsQueue.getQueueRecordsArray(); ContureWall[] result=new ContureWall[queueRecordsArray.length]; for(int i=0; i < result.length; i++) result[i]=(ContureWall) queueRecordsArray[i]; return result;} public ContureWall[] getWalls() { return wallsQueueArr;} public void updateWalls(Rectangle2D area) { ContureWall[] itWalls=getWalls(); for(int i=0; i < itWalls.length; i++) itWalls[i].update(area);} public String toString() { return "WallsProfile [farSide="+farSide+", ceiling="+ceiling+", rightSide="+rightSide+", floor="+floor+", leftSide="+leftSide+", nearGlass="+nearGlass+"]";}}