package mydev.cs; import mydev.about.ColorfulShapeSet; import mydev.about.Colorfull; import mydev.about.Rectangle2D; public abstract class WallsProfile extends Colorfull { private ColorfulShapeSet wallsQueue=new ColorfulShapeSet(); protected ContureWall farSide; protected ContureWall ceiling; protected ContureWall rightSide; protected ContureWall floor; protected ContureWall leftSide; protected ContureWall nearGlass; public WallsProfile(int contureColor) { super(contureColor); initWallsQueue(contureColor);} protected void initWallsQueue(int contureColor) { wallsQueue.append(farSide=obtainFarSide(contureColor)); wallsQueue.append(ceiling=obtainCeiling(contureColor)); wallsQueue.append(rightSide=obtainRightSide(contureColor)); wallsQueue.append(floor=obtainFloor(contureColor)); wallsQueue.append(leftSide=obtainLeftSide(contureColor)); wallsQueue.append(nearGlass=obtainNearGlass(contureColor));} protected abstract ContureWall obtainFarSide(int contureColor); protected abstract ContureWall obtainCeiling(int contureColor); protected abstract ContureWall obtainRightSide(int contureColor); protected abstract ContureWall obtainFloor(int contureColor); protected abstract ContureWall obtainLeftSide(int contureColor); protected abstract ContureWall obtainNearGlass(int contureColor); public ContureWall getFarSide() { return farSide;} public ContureWall getCeiling() { return ceiling;} public ContureWall getRightSide() { return rightSide;} public ContureWall getFloor() { return floor;} public ContureWall getLeftSide() { return leftSide;} public ContureWall getNearGlass() { return nearGlass;} public ContureWall[] getWalls() { Colorfull[] cfItemsArray=wallsQueue.toArray(); ContureWall[] result=new ContureWall[cfItemsArray.length]; for(int i=0; i < result.length; i++) result[i]=(ContureWall) cfItemsArray[i]; return result;} public void updateWalls(Rectangle2D area) { ContureWall[] itWalls=getWalls(); for(int i=0; i < itWalls.length; i++) itWalls[i].update(area);}}