package mydev.cs; import mydev.about.Colorfull; import mydev.about.Rectangle2D; import mydev.vutils.Queue; public abstract class WallsProfile extends Colorfull { private Queue wallSet=new Queue(); protected ContureWall farSide; protected ContureWall ceiling; protected ContureWall rightSide; protected ContureWall floor; protected ContureWall leftSide; protected ContureWall nearGlass; public WallsProfile(int contureColor) { super(contureColor); createWallSet(contureColor);} protected void createWallSet(int contureColor) { wallSet.enqueueQueueRecord(farSide=obtainFarSide(contureColor)); wallSet.enqueueQueueRecord(ceiling=obtainCeiling(contureColor)); wallSet.enqueueQueueRecord(rightSide=obtainRightSide(contureColor)); wallSet.enqueueQueueRecord(floor=obtainFloor(contureColor)); wallSet.enqueueQueueRecord(leftSide=obtainLeftSide(contureColor)); wallSet.enqueueQueueRecord(nearGlass=obtainNearGlass(contureColor));} protected abstract ContureWall obtainFarSide(int contureColor); protected abstract ContureWall obtainCeiling(int contureColor); protected abstract ContureWall obtainRightSide(int contureColor); protected abstract ContureWall obtainFloor(int contureColor); protected abstract ContureWall obtainLeftSide(int contureColor); protected abstract ContureWall obtainNearGlass(int contureColor); public ContureWall getFarSide() { return farSide;} public ContureWall getCeiling() { return ceiling;} public ContureWall getRightSide() { return rightSide;} public ContureWall getFloor() { return floor;} public ContureWall getLeftSide() { return leftSide;} public ContureWall getNearGlass() { return nearGlass;} public ContureWall[] wallsIterator() { Object[] queueRecordsArray=wallSet.getQueueRecordsArray(); ContureWall[] result=new ContureWall[queueRecordsArray.length]; for(int i=0; i < result.length; i++) result[i]=(ContureWall) queueRecordsArray[i]; return result;} public void updateWalls(Rectangle2D area) { ContureWall[] itWalls=wallsIterator(); for(int i=0; i < itWalls.length; i++) { ContureWall curWall=(ContureWall) itWalls[i]; curWall.update(area);}}}