package mydev.csprofile; public class VisibilityProfile { protected boolean wallFar; protected boolean wallTop; protected boolean wallRight; protected boolean wallDown; protected boolean wallLeft; protected boolean wallFront; public VisibilityProfile() { this(true,true,true,true,true,true); } public VisibilityProfile(boolean wallFar,boolean wallTop,boolean wallRight,boolean wallDown,boolean wallLeft,boolean wallFront) { super(); this.wallFar=wallFar; this.wallTop=wallTop; this.wallRight=wallRight; this.wallDown=wallDown; this.wallLeft=wallLeft; this.wallFront=wallFront;} public boolean isWallFar() { return wallFar;} public VisibilityProfile buildFarSide(boolean wallFar) { this.wallFar=wallFar; return this;} public boolean isWallTop() { return wallTop;} public VisibilityProfile buildWallUp(boolean wallTop) { this.wallTop=wallTop; return this;} public boolean isWallRight() { return wallRight;} public VisibilityProfile buildWallRight(boolean wallRight) { this.wallRight=wallRight; return this;} public boolean isWallDown() { return wallDown;} public VisibilityProfile buildWallDown(boolean wallDown) { this.wallDown=wallDown; return this;} public boolean isWallLeft() { return wallLeft;} public VisibilityProfile buildWallLeft(boolean wallLeft) { this.wallLeft=wallLeft; return this;} public boolean isWallNear() { return wallFront;} public VisibilityProfile buildNearSide(boolean wallFront) { this.wallFront=wallFront; return this;} public String toString() { return "VisibilityProfile [wallFar="+wallFar+", wallTop="+wallTop+", wallRight="+wallRight+", wallDown="+wallDown+", wallLeft="+wallLeft+", wallFront="+wallFront+"]";}}