package mydev.im12x; public class FetchImageParty { protected ImageParty ref; protected int curPartIdx; public FetchImageParty(ImageParty ref) { super(); this.ref=ref; this.curPartIdx=0;} public boolean hasNext() { return curPartIdx < ref.getPartsNum();} public int[] next() { int[] part=(int[]) ref.getPart(curPartIdx++); return part;} public int getTotalLength() { return ref.getTotal();} public int getWidth() { return ref.getScanSize();}}