package mydev.laki; import mydev.vutils.Time; import mydev.vutils.Ester; public class LowPrime { public LowPrime() {} public void proc() { int count=79999; int[] data=new int[count+2]; data[0]=2; data[1]=3; int idx=2; int curPattern=3; while(count > 0) { curPattern+=2; if(curPattern > 1000010) break; boolean flag=true; for(int i=0; i < idx; i++) if(curPattern / data[i] * data[i]==curPattern) { flag=false; break;} if(flag) { data[idx++]=curPattern; count--;}} Ester buf=new Ester(""); boolean ff=true; for(int i=1; i <= idx; i++) { String first=ff ? " " : "   "; int lka=6-(""+data[i-1]).length(); if(lka > 0) { for(int j=0; j < lka; j++) System.out.print(" ");} buf.append(ff ? "x" : " 0x"); int lka2=6-(""+Integer.toHexString(data[i-1])).length(); if(lka2 > 0) { for(int j=0; j < lka2; j++) buf.append("0");} ff=false; String item=first+data[i-1]; buf.append(Integer.toHexString(data[i-1]).toUpperCase()); if(i / 8 * 8==i) { System.out.println(item); System.out.println(buf.toString()); buf=new Ester(""); ff=true;} else { System.out.print(item);}} String last=buf.toString(); if(last.length() > 0) { System.out.println(""); System.out.print(last);} System.out.println(""); System.out.println("__Totale_:_"+idx+"_shtukens_(wave)");} public static void main(String[] args) { LowPrime lp=new LowPrime(); Time tm=new Time(); tm.start(); lp.proc(); tm.trust(); System.out.println("__Za_:_"+tm.ess());}}
