package mydev.m3d; import java.util.Enumeration; import java.util.Hashtable; public class VertExModel { protected Hashtable vertices; public VertExModel() { super(); this.vertices=new Hashtable();} public void setActivationListener(ActivationListener listener) { Enumeration elements=vertices.elements(); while(elements.hasMoreElements()) { VertEx3D vert=(VertEx3D) elements.nextElement(); vert.setListener(listener);}} public void addVert(VertEx3D vert) { if(vert !=null) vertices.put(vert.name,vert);} public int getNum() { return vertices.size();} public VertEx3D getVert(String name) { if(name !=null) { VertEx3D vert=(VertEx3D) vertices.get(name); return vert;} return null;} public VertEx3D[] getAll() { VertEx3D[] result=new VertEx3D[getNum()]; int i=0; Enumeration elements=vertices.elements(); while(elements.hasMoreElements()) { VertEx3D vert=(VertEx3D) elements.nextElement(); result[i++]=vert;} return result;}}