package mydev.m3d; import java.util.Enumeration; import java.util.Hashtable; import mydev.about.ColorsArray; public class VertExModel { public static final int CELL=18+1; public static final int USUAL=ColorsArray.bluePoint.ic(); public static final int ACTIVATION=ColorsArray.yellowPoint.ic(); public static final int PRECICE=ColorsArray.whitePoint.ic(); protected Hashtable vertices=new Hashtable(); protected int halfSide=CELL; protected int objColor=USUAL; protected int objActivationColor=ACTIVATION; protected int projectionsColor=PRECICE; public VertExModel() { super();} public VertExModel(int halfSide,int objColor,int objActivationColor,int projectionsColor) { super(); this.halfSide=halfSide; this.objColor=objColor; this.objActivationColor=objActivationColor; this.projectionsColor=projectionsColor;} public void setActivationListener(ActivationListener listener) { Enumeration elements=vertices.elements(); while(elements.hasMoreElements()) { VertEx3D vert=(VertEx3D) elements.nextElement(); vert.setListener(listener);}} public ActivationListener getActivationListener() { if(vertices !=null) { Enumeration elements=vertices.elements(); if(elements.hasMoreElements()) return((VertEx3D) elements.nextElement()).getListener();} return null;} public void addVert(VertEx3D vert) { if(vert !=null) vertices.put(vert.name,vert);} public void addVerts(VertEx3D[] verts) { if(verts !=null) for(int i=0; i < verts.length; i++) vertices.put(verts[i].name,verts[i]);} public int getNum() { return vertices.size();} public VertEx3D getVert(String name) { if(name !=null) { VertEx3D vert=(VertEx3D) vertices.get(name); return vert;} return null;} public VertEx3D[] getAll() { VertEx3D[] result=new VertEx3D[getNum()]; int i=0; Enumeration elements=vertices.elements(); while(elements.hasMoreElements()) { VertEx3D vert=(VertEx3D) elements.nextElement(); result[i++]=vert;} return result;}}