package mydev.m3d; import java.util.Hashtable; public abstract class VertExSettings { protected Hashtable regItems=new Hashtable(); public VertExSettings() { super();} public boolean hasPro(String name) { Object result=regItems.get(name); if(result !=null) { if(result instanceof Boolean) return((Boolean) result).equals(true); else return true;} return false;} public void setPro(String name,boolean value) { regItems.put(name,new Boolean(value));} public void setPro(String name,Object value) { regItems.put(name,value);} public Object getPro(String name) { Object result=regItems.get(name); return result;}}