package mydev.m3d; import mydev.vutils.Karta; public abstract class VertExSettings { protected Karta regItems; public VertExSettings() { super(); this.regItems=new Karta();} public boolean hasPro(String name) { Object result=regItems.get(name); if(result !=null) { if(result instanceof Boolean) return((Boolean) result).equals(Boolean.TRUE); else return true;} return false;} public void setPro(String name,boolean value) { regItems.put(name,new Boolean(value));} public void setPro(String name,Object value) { regItems.put(name,value);} public Object getPro(String name) { Object result=regItems.get(name); return result;}}