package mydev.planelook; public class CurPtr { private short mx; private short my; public CurPtr(int mx,int my) { super(); this.mx=(short) mx; this.my=(short) my;} public CurPtr(short mx,short my) { super(); this.mx=mx; this.my=my;} public int dx(int x) { return mx-x;} public int dx(CurPtr msCaptured) { return msCaptured.mx-mx;} public int dy(int y) { return my-y;} public int dy(CurPtr msCaptured) { return my-msCaptured.my;} public short mx() { return mx;} public short my() { return my;} public short getMx() { return mx;} public short getMy() { return my;} public String toString() { return "Mouse("+mx+", "+my+")";}}