package mydev.planelook; import java.awt.Rectangle; public class MouseClickEvent extends MouseMoveEvent { private boolean down; private boolean ctrlMask; public MouseClickEvent(CurPtr ms,Rectangle rt,boolean down,boolean ctrlMask) { super(ms,rt); this.down=down; this.ctrlMask=ctrlMask;} public MouseClickEvent(CurPtr ms,Rectangle rt,boolean down) { super(ms,rt); this.down=down;} public boolean isCtrlMask() { return ctrlMask;} public boolean getDown() { return down;} public String toString() { return "MouseClickEvent [down="+down+", ctrlMask="+ctrlMask+"]";}}