package mydev.planelook; import java.awt.Rectangle; public class MouseMoveEvent { protected CurPtr ms; protected Ut rx; protected Ut ry; protected Pika ovchick; public MouseMoveEvent(CurPtr ms,Rectangle rt) { super(); this.ms=ms; this.rx=new Ut(rt.x,rt.x+rt.width); this.ry=new Ut(rt.y,rt.y+rt.height); this.ovchick=new Pika(rt.width,rt.height);} public CurPtr ms() { return ms;} public CurPtr getMs() { return ms;} public Ut getRx() { return rx;} public Ut getRy() { return ry;} public Pika getOvchick() { return ovchick;} public String toString() { return "MouseMoveEvent [ms="+ms+", rx="+rx+", ry="+ry+", ovchick="+ovchick+"]";}}