package mydev.planelook; import mydev.uix.UpdateConsumer; public class WireCoord { public static boolean upper=false; public final static WireCoord xC=new WireCoord("x"); public final static WireCoord yC=new WireCoord("y"); public final static WireCoord zC=new WireCoord("z"); public final static WireCoord nC=new WireCoord(""); private String name; private String value; private UpdateConsumer consumer; private WireCoord(String name) { super(); this.name=upper ? name.toUpperCase() : name;} public void bindConsumer(UpdateConsumer consumer) { this.consumer=consumer;} public String getName() { return name;} public String getValue() { return value;} public void setValue(String value) { this.value=value; if(consumer !=null) consumer.consume(value);} public WireCoord complicate() { return new WireCoord(name);} public String toString() { return ""+name+": "+value;}}