package mydev.sqproj; import mydev.about.CanvasAdapter; import mydev.about.FourCornersConture2D; import mydev.about.PaintAdapter; import mydev.cs.Bounds; public class SqProj extends FourCornersConture2D { public SqProj(int color) { super(color);} public SqProj(int x1,int y1,int x2,int y2,int x3,int y3,int x4,int y4,int c1) { super(x1,y1,x2,y2,x3,y3,x4,y4,c1);} public void encryptSimple() { super.encryptSimple();} public void toPen(CanvasAdapter ics,PaintAdapter pn) { super.toPen(ics,pn.select(this));} public int width() { Bounds cpb=getBounds(); return cpb.getRightX()-cpb.getLeftX();} public int height() { Bounds cpb=getBounds(); return cpb.getBottomY()-cpb.getTopY();} public Bounds getBounds() { Bounds cpb=new Bounds(); cpb.setLeftX((short) getMinX()); cpb.setRightX((short) getMaxX()); cpb.setTopY((short) getMinY()); cpb.setBottomY((short) getMaxY()); return cpb;} public SqProj external() { Bounds cpb=getBounds(); return new SqProj(cpb.getLeftX(),cpb.getTopY(),cpb.getRightX(),cpb.getTopY(),cpb.getRightX(),cpb.getBottomY(),cpb.getLeftX(),cpb.getBottomY(),color);} public SqProj normalize() { Bounds cpb=getBounds(); return new SqProj(getP1().getX()-cpb.getLeftX(),getP1().getY()-cpb.getTopY(),getP2().getX()-cpb.getLeftX(),getP2().getY()-cpb.getTopY(),getP3().getX()-cpb.getLeftX(),getP3().getY()-cpb.getTopY(),getP4().getX()-cpb.getLeftX(),getP4().getY()-cpb.getTopY(),color);} public static SqProj create(FourCornersConture2D sqProj,boolean turnBack) { if(turnBack) return new SqProj(sqProj.getP4().getX(),sqProj.getP4().getY(),sqProj.getP1().getX(),sqProj.getP1().getY(),sqProj.getP2().getX(),sqProj.getP2().getY(),sqProj.getP3().getX(),sqProj.getP3().getY(),sqProj.getColor()); return new SqProj(sqProj.getP3().getX(),sqProj.getP3().getY(),sqProj.getP2().getX(),sqProj.getP2().getY(),sqProj.getP1().getX(),sqProj.getP1().getY(),sqProj.getP4().getX(),sqProj.getP4().getY(),sqProj.getColor());}}