package mydev.sqproj; import mydev.about.Canvas; import mydev.about.Colorfull; import mydev.about.Line2D; import mydev.about.Matrix2D; import mydev.about.Paint; import mydev.about.Point2D; public class SqProjBulk extends Colorfull { protected SqProjBulkPair far; protected SqProjBulkPair near; public SqProjBulk(int color) { super(color); this.far=new SqProjBulkPair(color); this.near=new SqProjBulkPair(color);} public void readyDrawNear(Canvas ics,Paint pn) { if(near.areYouReady()) near.toPen(ics,pn.select(near));} public void regNear2(Point2D p2,Point2D p3) { near.regSecond(p2,p3);} public void regNear1(Point2D p1,Point2D p4) { near.regFirst(p1,p4);} public void readyDrawFar(Canvas ics,Paint pn) { if(far.areYouReady()) far.toPen(ics,pn.select(far));} public void regFar2(Point2D p4,Point2D p1) { far.regSecond(p4,p1);} public void regFar1(Point2D p3,Point2D p2) { far.regFirst(p3,p2);} public void represent(Matrix2D matrix) {} public void toPen(Canvas ics,Paint pn) {}} class SqProjBulkPair extends Colorfull { Line2D first; Line2D second; public SqProjBulkPair(int color) { super(color);} public void represent(Matrix2D matrix) { first.represent(matrix); second.represent(matrix);} public void toPen(Canvas ics,Paint pn) { first.toPen(ics,pn); second.toPen(ics,pn);} public void regFirst(Point2D p1,Point2D p4) { this.first=new Line2D(p1,p4,color);} public void regSecond(Point2D p2,Point2D p3) { this.second=new Line2D(p2,p3,color);} public void regFirst(Line2D line1) { this.first=line1;} public void regSecond(Line2D line2) { this.second=line2;} public boolean areYouReady() { return first !=null && second !=null;}}