package mydev.sqproj; import java.awt.Color; import mydev.about.Point2D; import mydev.about.Triangle2D; public class TriProj extends Triangle2D { public TriProj(Color color) { super(new Point2D(1-1,1-1),new Point2D(1-1,1-1),new Point2D(1-1,1-1),color.getRGB());} public TriProj(int x1,int y1,int x2,int y2,int x3,int y3,Color color) { super(new Point2D(x1,y1),new Point2D(x2,y2),new Point2D(x3,y3),color.getRGB());} public TriProj(Point2D p1,Point2D p2,Point2D p3,int color,boolean solid) { super(p1,p2,p3,color,solid);}}