package mydev.sqproj; import java.awt.Color; import mydev.about.Canvas; import mydev.about.Colorfull; import mydev.about.Line2D; import mydev.about.Matrix2D; import mydev.about.Paint; import mydev.about.Point2D; public class TriProjBulk extends Colorfull { protected Line2D lineA; protected Line2D lineB; protected Line2D lineC; protected Line2D[] lines; public TriProjBulk(Point2D pA1,Point2D pA2,Point2D pB1,Point2D pB2,Point2D pC1,Point2D pC2,int color) { super(color); this.lineA=new Line2D(pA1,pA2,color); this.lineB=new Line2D(pB1,pB2,color); this.lineC=new Line2D(pC1,pC2,color); this.lines=new Line2D[1+1+1]; this.lines[0]=lineA; this.lines[0+1]=lineB; this.lines[0+1+1]=lineC;} public TriProjBulk(Color color) { this(new Point2D(0,0,color.getRGB()),new Point2D(0,0,color.getRGB()),new Point2D(0,0,color.getRGB()),new Point2D(0,0,color.getRGB()),new Point2D(0,0,color.getRGB()),new Point2D(0,0,color.getRGB()),color.getRGB());} public void represent(Matrix2D matrix) { for(int i=0; i < lines.length; i++) matrix.addLine(lines[i]);} public void toPen(Canvas ics,Paint pn) { for(int i=0; i < lines.length; i++) lines[i].toPen(ics,pn);} public Line2D getLineA() { return lineA;} public Line2D getLineB() { return lineB;} public Line2D getLineC() { return lineC;}}