package mydev.uix; import java.awt.Color; import java.awt.FlowLayout; import java.awt.Label; import java.awt.Panel; import java.awt.TextField; public class AmountPanel extends Panel { private Label lbCriteria; private TextField tfPos; private TextField tfAmount; private Label lbItems; private String prevPos; private String prevAmount; public AmountPanel(String criteriaName,String itemsName) { super(); setLayout(getPanelLayout()); setBackground(getPanelBgColor()); if(criteriaName !=null && criteriaName.length() > 0) add(lbCriteria=new Label(criteriaName)); add(tfPos=new TextField(getPosWidth())); tfPos.setEditable(false); add(tfAmount=new TextField("",getAmountWidth())); if(itemsName !=null && itemsName.length() > 0) add(lbItems=new Label(""+itemsName)); tfAmount.setEditable(false); clearPanel();} public Label getLbCriteria() { return lbCriteria;} public TextField getTfPos() { return tfPos;} public TextField getTfAmount() { return tfAmount;} public Label getLbItems() { return lbItems;} protected int getPosWidth() { return 5+1+1;} protected int getAmountWidth() { return 5+1+1;} public Color getPanelBgColor() { return Color.gray;} public FlowLayout getPanelLayout() { return new FlowLayout(FlowLayout.CENTER);} public void clearPanel() { if(tfAmount !=null) tfAmount.setText(""); if(tfPos !=null) tfPos.setText(""); prevPos=""; prevAmount="";} public void setPos(String pos) { if(pos !=null) { if(prevPos !=null && prevPos.compareTo(pos)==0) prevPos=pos; else { prevPos=pos; if(tfPos !=null) tfPos.setText(pos);}} else { prevPos=""; if(tfPos !=null) tfPos.setText("");}} public void setAmount(String amount) { if(amount !=null) { if(prevAmount !=null && prevAmount.compareTo(amount)==0) prevAmount=amount; else { prevAmount=amount; if(tfAmount !=null) tfAmount.setText(amount);}} else { prevAmount=""; if(tfAmount !=null) tfAmount.setText("");}}}