package mydev.uix; import mydev.about.Bitmap; public abstract class AnimationFrame { protected int[] pixels; protected int width; protected int height; protected Bitmap bitmap; protected AnimationFrame() { super();} protected abstract int getWidth(); protected abstract int getHeight(); protected abstract int[] initData(); public Bitmap getImage() { if(bitmap==null) { if(pixels==null) { width=getWidth(); height=getHeight(); pixels=initData();} bitmap=new Bitmap(pixels,(short) width,(short) height);} return bitmap;} public String toString() { return "AnimationFrame [pixelsLength="+(pixels !=null ? pixels.length : 0)+", width="+width+", height="+height+", bitmap="+bitmap+"]";}}