package mydev.uix; import java.awt.Component; import java.awt.Image; import mydev.about.Bitmap; public abstract class AnimationFrame { protected int[] pixels; protected int width; protected int height; protected Image img; protected AnimationFrame() { super();} protected abstract int getWidth(); protected abstract int getHeight(); protected abstract int[] initData(); public Image getImage(Component ctx) { if(img==null) { if(pixels==null) { width=getWidth(); height=getHeight(); pixels=initData();} Bitmap bitmap=new Bitmap(pixels,(short) width,(short) height); this.img=bitmap.getImage(ctx);} return img;} public String toString() { return "AnimationFrame [pixelsLength="+(pixels !=null ? pixels.length : 0)+", width="+width+", height="+height+", img="+img+"]";}}