package mydev.uix; import java.awt.Button; import java.awt.Checkbox; import java.awt.FlowLayout; import java.awt.Label; import java.awt.TextField; public abstract class CommonPathPanel extends ControlsPanel { final static String LB_PATH="lbPath"; final static String TF_PATH="tfPath"; final static String BT_STOP="btStop"; final static String BT_SCAN="btScan"; final static String QP_MULTY="qpMulty"; final static String CH_PREVIEW="chPreview"; final static String NAME_SCAN="Scan"; final static String NAME_STOP="Stop"; final static String NAME_PREVIEW="Preview&Close"; protected boolean longTermOp; protected boolean withCommonCtrls; protected boolean withMulty; public CommonPathPanel(boolean withCommonCtrls,boolean withMulty) { super(); setLayout(getPanelLayout()); setBackground(getPanelBgColor()); this.withCommonCtrls=withCommonCtrls; this.withMulty=withMulty; if(withCommonCtrls) buildCommonCtrls(withMulty); disableStop();} public FlowLayout getPanelLayout() { return new FlowLayout();} public void enableCtrls(boolean enable) { if(getTfPath() !=null) getTfPath().enable(enable); if(getBtScan() !=null) getBtScan().enable(enable); if(getMultyButton() !=null) getMultyButton().enableCtrl(enable); if(getChPreview() !=null) getChPreview().enable(enable);} protected abstract ToogleMultyButton obtainMultyButton(); protected abstract Label obtainLbPath(); protected abstract TextField obtainTfPath(); protected String getScanLabel() { return NAME_SCAN;} protected String getStopLabel() { return NAME_STOP;} protected String getPreviewLabel() { return NAME_PREVIEW;} protected void buildCommonCtrls(boolean withMulty) { buildBtScan(); buildBtStop(); if(withMulty) buildMulty(); buildChPreview();} protected void buildPath() { Label lbPath=obtainLbPath(); append(LB_PATH,lbPath); TextField tfPath=obtainTfPath(); append(TF_PATH,tfPath);} protected void buildChPreview() { Checkbox chPreview=new Checkbox(getPreviewLabel()); append(CH_PREVIEW,chPreview); chPreview.setState(false);} protected void buildMulty() { append(QP_MULTY,obtainMultyButton());} protected void buildBtStop() { Button btStop=new Button(getStopLabel()); append(BT_STOP,btStop); btStop.enable(false);} protected void buildBtScan() { Button btScan=new Button(getScanLabel()); append(BT_SCAN,btScan);} public void disableStop() { enableCtrls(true); if(withCommonCtrls) getBtStop().enable(!true); longTermOp=false;} public void enableStop() { enableCtrls(false); if(withCommonCtrls) getBtStop().enable(!false); longTermOp=true;} public boolean getPreview() { return((Checkbox) getCtrl(CH_PREVIEW)).getState();} public void setPreview(boolean preview) {((Checkbox) getCtrl(CH_PREVIEW)).setState(preview);} public boolean getMultyState() { return((ToogleMultyButton) getCtrl(QP_MULTY)).getState();} public void changeMultyState() {((ToogleMultyButton) getCtrl(QP_MULTY)).changeState();} public void enableMultyBtn(boolean enable) { getMultyButton().enableCtrl(enable);} public void enablePreview(boolean enable) {((Checkbox) getCtrl(CH_PREVIEW)).enable(enable);} public void setPath(String path) {((TextField) getCtrl(TF_PATH)).setText(path);} public String getPath() { return((TextField) getCtrl(TF_PATH)).getText();} public ToogleMultyButton getMultyButton() { return(ToogleMultyButton) getCtrl(QP_MULTY);} public TextField getTfPath() { return(TextField) getCtrl(TF_PATH);} public Button getBtMulty() { return getMultyButton().getMultyBtn();} public Button getBtStop() { return(Button) getCtrl(BT_STOP);} public Button getBtScan() { return(Button) getCtrl(BT_SCAN);} public Checkbox getChPreview() { return(Checkbox) getCtrl(CH_PREVIEW);}}