package mydev.uix; import java.awt.BorderLayout; import java.awt.Color; import java.awt.Component; import java.awt.LayoutManager; import java.awt.Panel; public class GeoLayoutBuilder { private Component northPanel; private Component southPanel; private Component westPanel; private Component eastPanel; private Component centerPanel; private Color bgColor; public static GeoLayoutBuilder create() { return new GeoLayoutBuilder();} public GeoLayoutBuilder appendNorth(Component panel) { this.northPanel=panel; return this;} public GeoLayoutBuilder appendSouth(Component panel) { this.southPanel=panel; return this;} public GeoLayoutBuilder appendWest(Component panel) { this.westPanel=panel; return this;} public GeoLayoutBuilder appendEast(Component panel) { this.eastPanel=panel; return this;} public GeoLayoutBuilder appendCenter(Component panel) { this.centerPanel=panel; return this;} public GeoLayoutBuilder bgColor(Color bgColor) { this.bgColor=bgColor; return this;} public GeoLayoutBuilder build(Panel panel) { panel.setLayout(getPanelLayout()); panel.setBackground(getPanelBgColor()); if(northPanel !=null) panel.add("North",northPanel); if(southPanel !=null) panel.add("South",southPanel); if(westPanel !=null) panel.add("West",westPanel); if(eastPanel !=null) panel.add("East",eastPanel); if(centerPanel !=null) panel.add("Center",centerPanel); return this;} public Color getPanelBgColor() { return this.bgColor;} public LayoutManager getPanelLayout() { int bs=getBorderSpace(); if(bs > 0) return new BorderLayout(bs,bs); return new BorderLayout();} public int getBorderSpace() { return 1-1;}}