package mydev.uix; import java.awt.BorderLayout; import java.awt.Checkbox; import java.awt.Color; import java.awt.Label; import java.awt.Panel; import java.awt.TextArea; public class IgnorePanel extends Panel { private TextArea taIgnoreList; private StopIgnorePanel stopIgnore; public IgnorePanel() { super(); setLayout(new BorderLayout()); Color bgColor=getPanelBgColor(); setBackground(bgColor); add("Center",taIgnoreList=new TextArea(4,4)); add("North",stopIgnore=new StopIgnorePanel(bgColor,getIgnoreLabel(),getCancelLabel())); taIgnoreList.setText("");} public void setIgnoreList(String ignoreList) { if(ignoreList !=null) taIgnoreList.setText(ignoreList);} public Color getPanelBgColor() { return Color.gray;} public String getIgnoreLabel() { return "Ignore list:";} public String getCancelLabel() { return "cancel ignore";} public TextArea getTaIgnoreList() { return taIgnoreList;} public StopIgnorePanel getStopIgnore() { return stopIgnore;} public void onCancelIgnore() { taIgnoreList.enable(!stopIgnore.isCancelIgnore());} public String getIgnoreList() { if(stopIgnore.isCancelIgnore()) return ""; return taIgnoreList.getText();} public Checkbox getCbCancelIgnore() { return stopIgnore.getCbCancelIgnore();} public void setCancelIgnore(boolean enable) { getCbCancelIgnore().setState(enable);} public boolean isCancelIgnore() { return getCbCancelIgnore().getState();} public void changeState() { boolean state=isCancelIgnore(); setCancelIgnore(!state); taIgnoreList.enable(state);}} class StopIgnorePanel extends Panel { private Checkbox cbCancelIgnore; public StopIgnorePanel(Color bgColor,String ignoreLabel,String cancelLabel) { super(); setLayout(new BorderLayout()); setBackground(bgColor); add("West",new Label(ignoreLabel)); add("East",cbCancelIgnore=new Checkbox(cancelLabel));} public Checkbox getCbCancelIgnore() { return cbCancelIgnore;} public boolean isCancelIgnore() { return cbCancelIgnore.getState();}}