package mydev.uix; import java.awt.Choice; import java.awt.Color; import java.awt.FlowLayout; import java.awt.Label; import java.awt.LayoutManager; import java.awt.Panel; import java.awt.TextField; public class LabeledEditPair extends Panel { public final static int DEF_WIDTH=5-1+5; private Label lbName; private Color panelBgColor=Color.white; protected TextField tfValue; protected Choice chVariants; public LabeledEditPair(String labelText,String editText,boolean enable) { this(labelText,editText,enable,DEF_WIDTH);} public LabeledEditPair(String labelText,String editText,int editWidth) { this(labelText,editText,true,editWidth);} public LabeledEditPair(String labelText,String editText) { this(labelText,editText,true,DEF_WIDTH);} public LabeledEditPair(String labelText,String editText,boolean enable,int editWidth) { super(); setBackground(panelBgColor); setLayout(getPanelLayout()); add(lbName=new Label(labelText)); add(tfValue=new TextField(editWidth)); tfValue.setText(editText); tfValue.enable(enable);} public LabeledEditPair(String labelText,Choice chCases) { super(); setBackground(panelBgColor); setLayout(getPanelLayout()); this.chVariants=chCases; add(lbName=new Label(labelText)); add(chCases);} public Color getPanelBgColor() { return panelBgColor;} public void setPanelBgColor(Color panelBgColor) { setBackground(panelBgColor); this.panelBgColor=panelBgColor;} public LayoutManager getPanelLayout() { return new FlowLayout(FlowLayout.CENTER);} public Label getLbName() { return lbName;} public Choice getChoisesCtrl() { return chVariants;} public String getText() { if(tfValue !=null) return tfValue.getText(); else if(chVariants !=null) return chVariants.getSelectedItem(); return "";} public void setText(String text) { if(text !=null) { if(tfValue !=null) tfValue.setText(text); else if(chVariants !=null) chVariants.select(text);}} public void enableCtrls(boolean enable) { if(tfValue !=null) tfValue.enable(enable); else if(chVariants !=null) chVariants.enable(enable);}}