package mydev.uix; import java.awt.BorderLayout; import java.awt.Color; import java.awt.List; import java.awt.Panel; public class ListPanel extends Panel { private AmountPanel amountPanelCda; protected List lsItems; public ListPanel(AmountPanel amountPanel) { super(); setLayout(new BorderLayout()); setBackground(getPanelBgColor()); this.amountPanelCda=amountPanel; createLayout();} public AmountPanel getAmountPanel() { return amountPanelCda;} public void addItem(String item) { lsItems.addItem(item);} public void addItem(String item,int pos) { lsItems.addItem(item,pos);} public void enableList(boolean enable) { lsItems.enable(enable);} public void clearItems() { lsItems.clear();} public Color getPanelBgColor() { return Color.lightGray;} public void setPosition(String pos) { if(amountPanelCda !=null) amountPanelCda.setPos(pos);} public void setAmount(String amount) { if(amountPanelCda !=null) amountPanelCda.setAmount(amount);} protected void createLayout() { if(amountPanelCda !=null) add("North",amountPanelCda); this.lsItems=new List(22,false); add("Center",lsItems);} public List getLsItems() { return lsItems;} public void selectItem(int idx) { lsItems.select(idx); if(amountPanelCda !=null && amountPanelCda.getTfPos() !=null) amountPanelCda.setPos(""+(idx+1));} public void selectNothing() { int idxSel=lsItems.getSelectedIndex(); if(idxSel >-1) { if(amountPanelCda !=null && amountPanelCda.getTfPos() !=null) amountPanelCda.setPos(""); lsItems.deselect(idxSel);}} public int getSelectedIndex() { return lsItems.getSelectedIndex();} public String getItemSelected() { String curSel=lsItems.getSelectedItem(); return curSel;} public String getItem(int idx) { return lsItems.getItem(idx);} public int getSelectedIdx() { int idxSel=lsItems.getSelectedIndex(); return idxSel;} public int itemsNum() { return lsItems.countItems();}}