package mydev.uix; import mydev.vutils.Karta; public class Params { private String name; private Karta params; public Params(String name) { super(); this.name=name; this.params=new Karta();} public void addParam(String name,String value) { this.params.put(name,value);} public void addParam(String name,Integer value) { this.params.put(name,value);} public String getName() { return name;} public String getParam(String name) { if(name !=null) return(String) params.get(name); return null;} public Integer getParamInteger(String name) { if(name !=null) return(Integer) params.get(name); return null;} public Object getParamObj(String name) { if(name !=null) return params.get(name); return null;} public boolean areYou(String cmdName) { boolean result=name.equalsIgnoreCase(cmdName); return result;}}