package mydev.uix; import java.awt.Color; import java.awt.FlowLayout; import java.awt.Label; import java.awt.Panel; import java.awt.TextField; public class SizePanel extends Panel { private Label lbSize; private TextField tfItemSize; private TextField tfTotalSize; public SizePanel(String sizeName) { this(sizeName,false);} public SizePanel(String sizeName,boolean withItemSize) { super(); setLayout(getPanelLayout()); setBackground(getPanelBgColor()); if(withItemSize) { add(tfItemSize=new TextField("",getItemSizeWidth())); tfItemSize.setEditable(false);} add(tfTotalSize=new TextField("",getTotalSizeWidth())); if(sizeName !=null && sizeName.length() > 0) add(lbSize=new Label(sizeName)); tfTotalSize.setEditable(false);} protected int getItemSizeWidth() { return 5+5;} protected int getTotalSizeWidth() { return 5+5;} public Color getPanelBgColor() { return Color.gray;} public FlowLayout getPanelLayout() { return new FlowLayout(FlowLayout.CENTER);} public void setTotalSize(String totalSize) { tfTotalSize.setText(totalSize);} public void setItemSize(String itemSize) { tfItemSize.setText(itemSize);} public Label getLbSize() { return lbSize;} public TextField getTfItemSize() { return tfItemSize;} public TextField getTfTotalSize() { return tfTotalSize;} public void clearPanel() { if(tfItemSize !=null) tfItemSize.setText(""); tfTotalSize.setText("");}}