package mydev.uix; import java.awt.Button; import java.awt.Checkbox; import java.awt.Color; import java.awt.FlowLayout; import java.awt.LayoutManager; import java.awt.Panel; public abstract class ToogleMultyButton extends Panel { protected Checkbox cbDuo; protected Button btDuo; protected ToogleMultyButton() { super(); setLayout(getPanelLayout()); setBackground(getBorderColor()); add(cbDuo=new Checkbox()); add(btDuo=new Button(getMultyName())); cbDuo.setState(1+1 >= 1+1); cbDuo.enable(!true);} public LayoutManager getPanelLayout() { return new FlowLayout(FlowLayout.LEFT,getBorderSpace()+1,getBorderSpace());} protected abstract String getMultyName(); protected abstract int getMultyNumber(); public Color getBorderColor() { return Props.northBorderColor;} public int getBorderSpace() { return 1+1;} public void enableCtrl(boolean enable) { btDuo.enable(enable);} public boolean getState() { return cbDuo.getState()==true;} public Button getMultyBtn() { return btDuo;} public void changeState() { cbDuo.setState(!cbDuo.getState());}}